import { expect, test } from 'vitest'

import { Bloom } from 'ox'

test('checks if bloom is valid', () => {
  expect(Bloom.isBloom('a5cc3c03994db5b0d9a5eEdD10Cabab0813678ac')).toBeFalsy()
  expect(
    Bloom.isBloom('0xa5cc3c03994db5b0d9a5eEdD10Cabab0813678ac'),
  ).toBeFalsy()
  expect(
    Bloom.isBloom(
      '',
    ),
  ).toBeFalsy()
  expect(
    Bloom.isBloom(
      '',
    ),
  ).toBeTruthy()
})
