import type * as Errors from '../../../Errors.js'
import { fromNumber } from '../../Hex/fromNumber.js'
import { Signature_extract } from '../../Signature/extract.js'
import { Signature_toRpc } from '../../Signature/toRpc.js'
import type { TransactionEip2930, TransactionEip2930_Rpc } from './types.js'

/**
 * Converts an {@link ox#Transaction.Eip2930} to an {@link ox#Transaction.Eip2930Rpc}.
 *
 * @example
 * ```ts twoslash
 * import { TransactionEip2930 } from 'ox'
 *
 * const transaction = TransactionEip2930.toRpc({
 *   accessList: [],
 *   blockHash:
 *     '0xc350d807505fb835650f0013632c5515592987ba169bbc6626d9fc54d91f0f0b',
 *   blockNumber: 19868015n,
 *   chainId: 1,
 *   from: '0x814e5e0e31016b9a7f138c76b7e7b2bb5c1ab6a6',
 *   gas: 278365n,
 *   hash: '0x353fdfc38a2f26115daadee9f5b8392ce62b84f410957967e2ed56b35338cdd0',
 *   input:
 *     '',
 *   gasPrice: 11985937556n,
 *   nonce: 855n,
 *   r: 44944627813007772897391531230081695102703289123332187696115181104739239197517n,
 *   s: 36528503505192438307355164441104001310566505351980369085208178712678799181120n,
 *   to: '0x3fc91a3afd70395cd496c647d5a6cc9d4b2b7fad',
 *   transactionIndex: 2,
 *   type: 'eip2930',
 *   v: 27,
 *   value: 700000000000000000n,
 *   yParity: 0,
 * })
 * ```
 *
 * @param transaction - The EIP-2930 transaction to convert.
 * @returns An RPC-formatted EIP-2930 transaction.
 */
export function TransactionEip2930_toRpc<pending extends boolean = false>(
  transaction: TransactionEip2930<pending>,
  _options: TransactionEip2930_toRpc.Options<pending> = {},
): TransactionEip2930_Rpc<pending> {
  const signature = Signature_extract(transaction)!

  return {
    accessList: transaction.accessList ?? [],
    blockHash: transaction.blockHash ?? null,
    blockNumber:
      typeof transaction.blockNumber === 'bigint'
        ? fromNumber(transaction.blockNumber)
        : null,
    chainId: fromNumber(transaction.chainId),
    data: transaction.input,
    from: transaction.from,
    gas: fromNumber(transaction.gas ?? 0n),
    gasPrice: fromNumber(transaction.gasPrice ?? 0n),
    hash: transaction.hash,
    input: transaction.input,
    nonce: fromNumber(transaction.nonce ?? 0n),
    to: transaction.to,
    transactionIndex: transaction.transactionIndex
      ? fromNumber(transaction.transactionIndex)
      : null,
    type: '0x1',
    value: fromNumber(transaction.value ?? 0n),
    ...Signature_toRpc(signature),
  } as TransactionEip2930_Rpc<pending>
}

export declare namespace TransactionEip2930_toRpc {
  type Options<pending extends boolean = false> = {
    pending?: pending | boolean | undefined
  }

  type ErrorType = Signature_extract.ErrorType | Errors.GlobalErrorType
}

TransactionEip2930_toRpc.parseError = (error: unknown) =>
  /* v8 ignore next */
  error as TransactionEip2930_toRpc.ErrorType
